const carouselCardItem = ({ item, index }) => {
    const isFirstItem = index === 0;
  
    return (
      <View style={[styles.container, isFirstItem && styles.firstItem]} key={index}>
        <Text style={styles.header}>{item.title}</Text>
        <Text style={styles.header}>{item.author}</Text>
      </View>
    );
  };
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#f0f8ff',
      width: ITEM_WIDTH,
      height: ITEM_WIDTH, // Make the height equal to the width
      paddingBottom: 100,
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 3,
      },
      shadowOpacity: 0.29,
      shadowRadius: 4.56,
      elevation: 7,
    },
    firstItem: {
      marginLeft: (Dimensions.get('window').width - ITEM_WIDTH) / 2, // Adjust the margin for the first item
    },
    header: {
      color: '#222',
      fontSize: 28,
      fontWeight: 'bold',
      paddingLeft: 20,
      paddingTop: 20,
    },
    body: {
      color: '#222',
      fontSize: 18,
      paddingLeft: 20,
      paddingLeft: 20,
      paddingRight: 20,
    },
  });
