import * as React from 'react';
import { FlatList, ImageBackground, ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import Flashcard from '../code/flashcards';

export default function LibrariesScreen({ navigation }) {

    const handleDeckPress = (deck) => {
        setSelectedDeck(deck);
      };

    return (
        <ImageBackground source={require('../assets/JungleBg.gif')} style={styles.backgroundImage}>
            <ScrollView >


                <View>
                <FlatList
                    data={decks.filter((deck) => deck.added === 'yes')}
                    horizontal
                    keyExtractor={(item) => item.id.toString()}
                    renderItem={({ item }) => (
                        <TouchableOpacity onPress={() => handleDeckPress(item)}>
                        <Text style={styles.deckTitle}>{item.name}</Text>
                        </TouchableOpacity>
                    )}
                    />
                </View>



            </ScrollView>
        </ImageBackground>
    );
}

const styles = StyleSheet.create({
    backgroundImage:{
        flex: 1,
        resizeMode: 'cover', // or 'stretch' or 'contain'
        justifyContent: 'center',
    },
    scrollContainer:{
        flexGrow: 1,
        backgroundColor: 'rgba(0, 0, 0, 0)', // Set the background color to transparent
        alignItems: 'center',
        justifyContent: 'center',
    },
    container: {
        flex: 1,
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
        padding: 20,
    },
    buttonContainer: {
        flexDirection: 'row',
        marginTop: 10, 
    },
    button: {
        backgroundColor: '#ECE3CE',
        paddingVertical: 15,
        paddingHorizontal: 30,
        borderRadius: 5,
        margin: 14,
        marginHorizontal: 20,
    },
    buttonText: {
        fontSize: 18,
        fontWeight: 'bold',
    },
});
