import * as React from 'react';
import { FlatList, ImageBackground, ScrollView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import Flashcard from '../code/flashcards';

export default function LibrariesScreen({ navigation }) {

    return (
        
            <ScrollView contentContainerStyle={styles.scrollContainer}>


                <View>
                    <View style={styles.container}>
                        <View style={styles.buttonContainer}>

                            <TouchableOpacity style={styles.button}>
                                <Text style={styles.buttonText}>Added</Text>
                            </TouchableOpacity>

                            <TouchableOpacity style={styles.button}>
                                <Text style={styles.buttonText}>Created</Text>
                            </TouchableOpacity>

                            <TouchableOpacity style={styles.button}>
                                <Text style={styles.buttonText}>Favorite</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </View>


            </ScrollView>
        
    );
}

const styles = StyleSheet.create({
    backgroundImage:{
        flex: 1,
        resizeMode: 'cover', // or 'stretch' or 'contain'
        justifyContent: 'center',
    },
    scrollContainer:{
        flexGrow: 1,
        backgroundColor: 'rgba(0, 0, 0, 0)', // Set the background color to transparent
        alignItems: 'center',
        justifyContent: 'center',
    },
    container: {
        flex: 1,
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
        padding: 10,
    },
    buttonContainer: {
        flexDirection: 'row',
        marginTop: 10, 
    },
    button: {
        backgroundColor: '#ECE3CE',
        paddingVertical: 15,
        paddingHorizontal: 30,
        borderRadius: 5,
        margin: 10,
        marginHorizontal: 8,
        top: -350,
    },
    buttonText: {
        fontSize: 16,
        fontWeight: 'bold',
    },
});
