import React from 'react';
import { View, Text, StyleSheet, Dimensions } from 'react-native';

export const SLIDER_WIDTH = 350;
export const ITEM_WIDTH = 300;

const carouselCardItem = ({ item, index }) => {
  return (
    <View style={styles.container} key={index}>
      <Text style={styles.header}>{item.title}</Text>
      <Text style={styles.header}>{item.author}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#f0f8ff',
    width: ITEM_WIDTH,
    height: ITEM_WIDTH, // Make the height equal to the width
    marginBottom: 20, // Adjust the marginBottom for spacing between items
    borderRadius: 10, // Add borderRadius for a modern look
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.23,
    shadowRadius: 2.62,
    elevation: 4,
  },
  header: {
    color: '#222',
    fontSize: 28,
    fontWeight: 'bold',
    paddingLeft: 20,
    paddingTop: 20,
  },
  body: {
    color: '#222',
    fontSize: 18,
    paddingLeft: 20,
    paddingRight: 20,
  },
});

export default carouselCardItem;
