import React, { useState } from 'react';
import { FlatList, ImageBackground, ScrollView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import Flashcard from '../code/flashcards';
import { decks } from '../code/data'; // Adjust the path accordingly

export default function LibrariesScreen({ navigation }) {
  const [filter, setFilter] = useState('Added'); // Default filter is 'Added'

  const filteredDecks = decks.filter(deck => deck[filter.toLowerCase()] === 'yes');

  const renderItem = ({ item }) => (
    <Text>{item.name}</Text>
  );

  return (
    <ImageBackground source={require('../assets/JungleBg.gif')} style={styles.backgroundImage}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View>
          <View style={styles.container}>
            <View style={styles.buttonContainer}>
              <TouchableOpacity
                style={styles.button}
                onPress={() => setFilter('Added')}
              >
                <Text style={styles.buttonText}>Added</Text>
              </TouchableOpacity>

              <TouchableOpacity
                style={styles.button}
                onPress={() => setFilter('Created')}
              >
                <Text style={styles.buttonText}>Created</Text>
              </TouchableOpacity>

              <TouchableOpacity
                style={styles.button}
                onPress={() => setFilter('Favorite')}
              >
                <Text style={styles.buttonText}>Favorite</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>

        <FlatList
          data={filteredDecks}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
      </ScrollView>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
    backgroundImage:{
        flex: 1,
        resizeMode: 'cover', // or 'stretch' or 'contain'
        justifyContent: 'center',
    },
    scrollContainer:{
        flexGrow: 1,
        backgroundColor: 'rgba(0, 0, 0, 0)', // Set the background color to transparent
        alignItems: 'center',
        justifyContent: 'center',
    },
    container: {
        flex: 1,
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
        padding: 10,
    },
    buttonContainer: {
        flexDirection: 'row',
        marginTop: 10,
        justifyContent: 'center', // Center the buttons horizontally
      },
      button: {
        backgroundColor: '#ECE3CE',
        paddingVertical: 15,
        paddingHorizontal: 30,
        borderRadius: 5,
        margin: 10,
    },
    buttonText: {
        fontSize: 16,
        fontWeight: 'bold',
    },
});
