import * as React from 'react';
import { ImageBackground, Picker, StyleSheet, Text, TextInput, View } from 'react-native';

export default function Create({ navigation }) {
  const [title, setTitle] = React.useState('');
  const [subjectCode, setSubjectCode] = React.useState('');
  const [selectedCourse, setSelectedCourse] = React.useState('');
  const [selectedSchool, setSelectedSchool] = React.useState('');
  const [selectedCategory, setSelectedCategory] = React.useState('');

  // Dummy data, replace with your actual data
  const courses = ['Course A', 'Course B', 'Course C'];
  const schools = ['School A', 'School B', 'School C'];
  const categories = ['Category A', 'Category B', 'Category C'];

  return (
    <ImageBackground
      source={require('../assets/JungleBg.gif')}
      style={styles.CreateContainer}
    >
      <Text style={styles.letsGetInto1}>Letâ€™s get into the (wood) work!</Text>

      <View style={styles.titlecontainer}>
        <Text style={styles.modalTitle}>Title</Text>
        <TextInput
          style={styles.modalInput}
          placeholder="Enter title"
          value={title}
          onChangeText={(text) => setTitle(text)}
        />
      </View>

      <View style={styles.titlecontainer}>
        <Text style={styles.modalTitle}>Subject Code</Text>
        <TextInput
          style={styles.modalInput}
          placeholder="Enter subject code"
          value={subjectCode}
          onChangeText={(text) => setSubjectCode(text.replace(/[^a-zA-Z0-9]/g, ''))}
        />
      </View>

      <View style={styles.titlecontainer}>
        <Text style={styles.modalTitle}>Course</Text>
        <Picker
          style={[styles.modalInput, { color: '#ECE3CE' }]}
          selectedValue={selectedCourse}
          onValueChange={(itemValue, itemIndex) => setSelectedCourse(itemValue)}
        >
          {courses.map((course, index) => (
            <Picker.Item key={index} label={course} value={course} />
          ))}
        </Picker>
      </View>

      <View style={styles.titlecontainer}>
        <Text style={styles.modalTitle}>School</Text>
        <Picker
          style={[styles.modalInput, { color: '#ECE3CE' }]}
          selectedValue={selectedSchool}
          onValueChange={(itemValue, itemIndex) => setSelectedSchool(itemValue)}
        >
          {schools.map((school, index) => (
            <Picker.Item key={index} label={school} value={school} />
          ))}
        </Picker>
      </View>

      <View style={styles.titlecontainer}>
        <Text style={styles.modalTitle}>Category</Text>
        <Picker
          style={[styles.modalInput, { color: '#ECE3CE' }]}
          selectedValue={selectedCategory}
          onValueChange={(itemValue, itemIndex) => setSelectedCategory(itemValue)}
        >
          {categories.map((category, index) => (
            <Picker.Item key={index} label={category} value={category} />
          ))}
        </Picker>
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  CreateContainer: {
    flex: 1,
    justifyContent: 'flex-start',
    resizeMode: 'cover',
  },
  letsGetInto1: {
    top: 20,
    alignSelf: 'center',
    fontSize: 15,
    letterSpacing: 1.6,
    fontWeight: '700',
    color: '#fff',
    textAlign: 'center',
    height: 33,
  },
  titlecontainer: {
    top: 20,
    padding: 10,
  },
  modalTitle: {
    fontSize: 20,
    color: '#ECE3CE',
    paddingBottom: 5,
  },
  modalInput: {
    height: 50,
    borderStyle: 'solid',
    borderColor: '#ece3ce',
    borderRadius: 5,
    borderWidth: 1,
    width: '90%',
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,
    paddingVertical: 5,
  },
});
